import React from "react";
import "./CreateBlog.scss";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { Formik, FormikHelpers } from "formik";

interface Values {
  title: string;
  description: string;
  date: Date;
  coverImage: string;

  content: "";
}

const CreateBlog = () => {
  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [
        { list: "ordered" },
        { list: "bullet" },
        { indent: "-1" },
        { indent: "+1" },
      ],
      ["link", "image"],
      ["clean"],
    ],
  };

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
  ];
  return (
    <div className="create-blog">
      <p className="create-blog-title">Create New Poste</p>
      <Formik
        initialValues={{
          title: "",
          description: "",
          date: "",
          coverImage: "",
          content: "",
        }}
        onSubmit={(
          values: Values,
          { setSubmitting }: FormikHelpers<Values>
        ) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 500);
        }}
      >
        <form>
          <input type="text" placeholder="Title" />

          <input type="text" placeholder="description" />

          <input type="date" />

          <input type="file" id="myFileInput" />

          <label htmlFor="myFileInput" className="custom-file-upload">
            Choose a file
          </label>

          <ReactQuill className="quill" modules={modules} formats={formats} />

          <div className="btn-create">
            <p>Create post</p>
          </div>
        </form>
      </Formik>
    </div>
  );
};

export default CreateBlog;
