import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = "";

export const getImage: any = createAsyncThunk(
  "type/image",
  async (coverImg: string) => {
    try {
      const response = await axios.get(`http://localhost:8080/${coverImg}`);
      return response.data;
    } catch (error: any) {
      console.error(error.response.data);
    }
  }
);

export const getImageSlice = createSlice({
  name: "Image",
  initialState,
  reducers: {},
});

export const {} = getImage.actions;

export default getImage.reducer;
