import React from "react";
import "./BlogCard.scss";

import workers from "../../../../assets/images/workers.jpg";
import right_arrow from "../../../../assets/icons/right-arrow.png";
import { Link } from "react-router-dom";
import { Post } from "../../../../data/slices/GetPostsSlice";

const BlogCard = ({ post }: any) => {
  return (
    <div className="blog-card">
      <div className="image">
        <img src={post.coverImg} alt=" " />
      </div>

      <p className="created-at">
        {post.createdAt.toLocaleDateString("en-US", Options)}
      </p>

      <Link to="/blog/id">
        <p className="blog-title">{post.title}</p>
      </Link>

      <p className="short-description">{post.description}</p>

      <div className="read-more">
        <p>Read More</p>
        <img src={right_arrow} alt="right arrow" />
      </div>
    </div>
  );
};

export default BlogCard;
