import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { Post } from "./GetPostsSlice";

interface PostDetailsState {
  post: Post;
  loading: boolean;
  error: string;
}

const initialState: PostDetailsState = {
  post: {
    title: "",
    description: "",
    coverImg: "",
    content: "",
    createdBy: "",
  },
  loading: false,
  error: "",
};

export const fetchPostDetails: any = createAsyncThunk(
  "type/fetchData",
  async (id) => {
    console.log("---", id);

    try {
      const response = await axios.get(
        `http://localhost:8080/api/v1/blogs/${id}`
      );
      console.log(response.data.data);

      return response.data.data;
    } catch (error) {
      console.log(error);
    }
  }
);

export const postListSlice = createSlice({
  name: "PostList",
  initialState,
  reducers: {},
  extraReducers: {
    [fetchPostDetails.pending]: (state) => {
      state.loading = true;
    },

    [fetchPostDetails.fulfilled]: (state, { payload }) => {
      state.loading = false;
      state.post = payload;
      console.log(payload);
    },

    [fetchPostDetails.rejected]: (state, { error }) => {
      state.loading = false;
      state.error = error;
    },
  },
});
