import React from "react";
import "./BlogCard.scss";

import workers from "../../../../assets/images/workers.jpg";
import right_arrow from "../../../../assets/icons/right-arrow.png";
import { Link } from "react-router-dom";
import { Post } from "../../../../data/slices/GetPostsSlice";

const BlogCard = ({ title, coverImg, description }: Post) => {
  return (
    <div className="blog-card">
      <div className="image">
        <img src={coverImg} alt=" " />
      </div>

      <p className="created-at">27 Jan 2021</p>

      <Link to="/blog/id">
        <p className="blog-title">{title}</p>
      </Link>

      <p className="short-description">{description}</p>

      <div className="read-more">
        <p>Read More</p>
        <img src={right_arrow} alt="right arrow" />
      </div>
    </div>
  );
};

export default BlogCard;
