import React from "react";
import "./CreateBlog.scss";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

interface Values {
  firstName: string;
  lastName: string;
  email: string;
}

const CreateBlog = () => {
  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [
        { list: "ordered" },
        { list: "bullet" },
        { indent: "-1" },
        { indent: "+1" },
      ],
      ["link", "image"],
      ["clean"],
    ],
  };

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
  ];
  return (
    <div className="create-blog">
      <p className="create-blog-title">Create New Poste</p>

      <form>
        <input type="text" placeholder="Title" />

        <input type="text" placeholder="description" />

        <input type="date" />

        <input type="file" id="myFileInput" />

        <label htmlFor="myFileInput" className="custom-file-upload">
          Choose a file
        </label>

        <ReactQuill className="quill" modules={modules} formats={formats} />

        <div className="btn-create">
          <p>Create post</p>
        </div>
      </form>
    </div>
  );
};

export default CreateBlog;
