import React from "react";
import "./CreateBlog.scss";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

const CreateBlog = () => {
  const modules = {
    toolbar: {
      container: "#toolbar",
    },
  };

  const formats = [
    "font",
    "size",
    "bold",
    "italic",
    "underline",
    "strike",
    "color",
    "background",
    "script",
    "header",
    "blockquote",
    "code-block",
    "indent",
    "list",
    "direction",
    "align",
    "link",
    "image",
    "video",
    "formula",
  ];
  return (
    <div className="create-blog">
      <p className="create-blog-title">Create New Poste</p>

      <form>
        <input type="text" placeholder="Title" />

        <input type="text" placeholder="description" />

        <input type="date" />

        <input type="file" id="myFileInput" />

        <label htmlFor="myFileInput" className="custom-file-upload">
          Choose a file
        </label>

        <ReactQuill
          className="quill"
          modules={modules}
          // formats={formats}
        />

        <div className="btn-create">
          <p>Create post</p>
        </div>
      </form>
    </div>
  );
};

export default CreateBlog;
