import { createSlice, PayloadAction } from "@reduxjs/toolkit";

export interface PostState {
  title: string;
  description: string;
  coverImg: string;
  content: string;
  createdBy: string;
}

const initialState: PostState = {
  title: "",
  description: "",
  coverImg: "",
  content: "",
  createdBy: "",
};

export const postSlice = createSlice({
  name: "Post",
  initialState,
  reducers: {
    createPost(state, action: PayloadAction<PostState>) {},
  },
  extraReducers: {},
});

export const {} = postSlice.actions;

export default postSlice.reducer;
