import React from "react";
import "./CreateBlog.scss";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { useFormik } from "formik";
import { useDispatch } from "react-redux";
import { Formik } from "formik";

import { Post, createPost } from "../../data/slices/CreatePostSlice";

const CreateBlog = () => {
  const dispatch = useDispatch();

  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [
        { list: "ordered" },
        { list: "bullet" },
        { indent: "-1" },
        { indent: "+1" },
      ],
      ["link", "image"],
      ["clean"],
    ],
  };

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
  ];

  const formik = useFormik({
    initialValues: {
      title: "",
      description: "",
      coverImg: "",
      content: "",
      createdAt: Date.now(),
    },
    onSubmit: async (values: Post) => {
      console.log(values);
      dispatch(createPost(values));
    },
  });
  return (
    <div className="create-blog">
      <p className="create-blog-title">Create New Post</p>
      <Formik>
        <form onSubmit={formik.handleSubmit}>
          <input
            type="text"
            placeholder="Title"
            name="title"
            onChange={formik.handleChange}
            value={formik.values.title}
          />

          <input
            type="text"
            placeholder="description"
            name="description"
            onChange={formik.handleChange}
            value={formik.values.description}
          />

          <input
            type="file"
            id="myFileInput"
            name="coverImg"
            onChange={formik.handleChange}
            value={formik.values.coverImg}
          />

          <label htmlFor="myFileInput" className="custom-file-upload">
            Choose a file
          </label>

          <ReactQuill
            className="quill"
            modules={modules}
            formats={formats}
            onChange={formik.handleChange}
            // name="content"
            value={formik.values.content}
          />

          <button type="submit" className="btn-create">
            <p>Create post</p>
          </button>
        </form>
      </Formik>
    </div>
  );
};

export default CreateBlog;
